/**
 * Interface to the {@link Storage} features that allows for storing and retrieving of objects by marshalling them.
 */
export default class Cache {
    /**
     * Check if sessionStorage is available for use.
     * @returns {boolean} availability - Whether or not caching features are available for use.
     */
    static isAvailable() {
        return typeof(Storage) !== "undefined";
    }

    /**
     * Store an object in cache, will not do anything of Cache is not available.
     * @param {string} key - The key for which the object can be retrieved.
     * @param {Object} object - The object to store.
     */
    static store(key, object) {
        if (this.isAvailable())
            window.sessionStorage.setItem(key, JSON.stringify(object));
    }

    /**
     * Check if an object is stored under key.
     * @param {string} key - The key for which the object can be retrieved.
     * @returns {boolean} isStored - Whether or not the object is stored.
     */
    static has(key) {
        return this.retrieve(key) !== null;
    }

    /**
     * Return the object stored.
     * @param {string} key - The key for which the object can be retrieved.
     * @returns {?Object} object - The stored object, null if storage not available or not stored under key.
     */
    static retrieve(key) {
        let object = null;
        if (this.isAvailable()) {
            object = sessionStorage.getItem(key);
            if (object !== null)
                object = JSON.parse(object);
        }
        return object;
    }

    /**
     * Remove the object at key, if it exists.
     * @param {string} key - The key for which the object can be retrieved.
     */
    static remove(key) {
        if (this.isAvailable())
            sessionStorage.removeItem(key);
    }
}

import $ from 'jquery';

/**
 * Presents small pop-up messages (notifications).
 * Does not repeat the same message if it currently displays it.
 */
export default class Notifier {
    /**
     * Initializes the previously displayed message field. Empty at creation.
     */
    constructor() {
        this.lastMessage = "";
    };

    /**
     * Notify the user with the supplied message.
     * @param {string} message - Message to be displayed. 
     */
    notify (message) {
        let snackbar = document.querySelector('.mdl-js-snackbar');
        if (this.lastMessage === message && $(snackbar).hasClass('mdl-snackbar--active'))
            return;
        snackbar.MaterialSnackbar.showSnackbar({message: message});
        this.lastMessage = message;
    };
};

import $ from 'jquery';
import config from './config';
import ZeeguuRequests from './zeeguuRequests';
import UserActivityLogger from './UserActivityLogger';

const USER_EVENT_STAR_ARTICLE = 'STAR ARTICLE';
const USER_EVENT_UNSTAR_ARTICLE = 'UNSTAR ARTICLE';
const OFF = 'off';
/**
 * Implements the functionality for starring an article, together with 
 * notifying Zeeguu about the changes.
 */
export default class Starer {
    /**
     * Initializes the state of the starer (default is false).
     */
    constructor(state=false) {
        this.on = state; 
    }

    /**
     * Sets the state of the starer.
     * @param {boolean} state - Defines the state for the starer: true - 'on' or false - 'off' 
     */
    setState(state) {
        this.on = state;
    }

    /**
     * Toggles the star on/off based on its current state and notifies
     * Zeeguu accordingly.
     */
    toggle() {
        let url = $(config.HTML_ID_ARTICLE_URL).children('a').attr('href');
        let title = $(config.HTML_ID_ARTICLE_TITLE).text();

        if (this.on) {
            // Launch Zeeguu request to notify about unstarring of article by user.
            ZeeguuRequests.post(config.POST_UNSTAR_ARTICLE, {url: url});
            UserActivityLogger.log(USER_EVENT_UNSTAR_ARTICLE, url, {title: title, language: FROM_LANGUAGE});

        } else { // it's off            
            // Launch Zeeguu request to notify about starring an article.
            ZeeguuRequests.post(config.POST_STAR_ARTICLE, {url: url, title: title, language_id: FROM_LANGUAGE});
            UserActivityLogger.log(USER_EVENT_STAR_ARTICLE, url, {title: title, language: FROM_LANGUAGE});
        }
        this._toggleState();
        this._toggleIcon();
    }

    _toggleState() {
        this.on = (this.on ? false : true);
    }

    _toggleIcon() {
        $(config.HTML_ID_TOGGLESTAR).children().each(function() {
            $(this).toggleClass(OFF);
        });
    }
}

import ZeeguuRequests from './zeeguuRequests'

const POST_USER_ACTIVITY_ENDPOINT = '/upload_user_activity_data';

/**
 * Abstracts logging a user event.
 * @see https://www.zeeguu.unibe.ch
 */
export default class UserActivityLogger {
    /**
     * Log a user event and send the information to zeeguu.
     * Uses {@link ZeeguuRequests}.
     * @param {string} event - The tag categorising the event. Always prepended with 'UMR'.
     * @param {string} value - Primary information of the event, can be empty.
     * @param {Object} extra_data - Optional additional information.
     */
    static log(event, value = '', extra_data = {}) {
        let date = new Date();
        let time = date.getUTCFullYear() + '-' + date.getUTCMonth() + '-' + date.getUTCDate()
                 + 'T' + date.getUTCHours() + ':' + date.getUTCMinutes() + ':' + date.getUTCMinutes();
        let event_information = {time: time,
                    event: 'UMR - ' + event,
                    value:value,
                    extra_data: JSON.stringify(extra_data)
        };
        ZeeguuRequests.post(POST_USER_ACTIVITY_ENDPOINT, event_information, this._onReply);
    }

    /**
     * Handle the reply from zeeguu.
     * @param {string} reply - Success status, 'OK' if all went well.
     */
    static _onReply(reply) {
        if (reply !== 'OK')
            console.log('Failed to record user activity, reply: ' + reply);
    }
}

import $ from 'jquery';
import Mustache from 'mustache';
import config from '../config';
import ZeeguuRequests from '../zeeguuRequests';
import Cache from '../Cache';
import NoFeedTour from './NoFeedTour';
import UserActivityLogger from '../UserActivityLogger';
import 'loggly-jslogger';

const KEY_MAP_FEED_ARTICLE = 'feed_article_map';
const USER_EVENT_CLICKED_ARTICLE = 'OPEN ARTICLE';

/* Setup remote logging. */
let logger = new LogglyTracker();
logger.push({
    'logglyKey': config.LOGGLY_TOKEN,
    'sendConsoleErrors' : true,
    'tag' : 'ArticleList'
});

/**
 * Manages a list of article links, stores them in {@link Cache} if possible.
 */
export default class ArticleList {
    /**
     * Initialise a {@link NoFeedTour} object.
     */
    constructor() {
        this.noFeedTour = new NoFeedTour();
    }

    /**
     * Call zeeguu and get the articles for the given feed 'subscription'.
     * If the articles are already in {@link Cache}, load them instead.
     * Uses {@link ZeeguuRequests}.
     * @param {Map} subscriptions - The feeds to retrieve articles from.
     */
    load(subscriptions) {
        if (subscriptions.size < 1)
            this.noFeedTour.show();
        else
            this.noFeedTour.hide();

        for (const subscription of subscriptions.values()) {
            if (Cache.has(KEY_MAP_FEED_ARTICLE) && Cache.retrieve(KEY_MAP_FEED_ARTICLE)[subscription.id]) {
                let articleLinks = Cache.retrieve(KEY_MAP_FEED_ARTICLE)[subscription.id];
                this._renderArticleLinks(subscription, articleLinks);
            } else {
                $(config.HTML_CLASS_LOADER).show();
                let callback = (articleLinks) => this._loadArticleLinks(subscription, articleLinks);
                ZeeguuRequests.get(config.GET_FEED_ITEMS + '/' + subscription.id, {}, callback);
            }
        }
    }

    /**
     * Remove all articles from the list.
     */
    clear() {
        $(config.HTML_ID_ARTICLELINK_LIST).empty();
    }

    /**
     * Remove all articles from the list associated with the given feed.
     * @param {string} feedID - The identification code associated with the feed.
     */
    remove(feedID) {
        $('li[articleLinkFeedID="' + feedID + '"]').remove();
    }

    /**
     * Store all the article links from a particular feed if possible, makes sure they are rendered.
     * Callback function for the zeeguu request.
     * @param {Object} subscription - The feed the articles are from.
     * @param {Object[]} articleLinks - List containing the articles for the feed.
     */
    _loadArticleLinks(subscription, articleLinks) {
        this._renderArticleLinks(subscription, articleLinks);
        $(config.HTML_CLASS_LOADER).fadeOut('slow');

        // Cache the article links.
        let feedMap = {};
        if (Cache.has(KEY_MAP_FEED_ARTICLE))
            feedMap = Cache.retrieve(KEY_MAP_FEED_ARTICLE);
        feedMap[subscription.id] = articleLinks;
        Cache.store(KEY_MAP_FEED_ARTICLE, feedMap);
    }

    /**
     * Generate all the article links from a particular feed.
     * @param {Object} subscription - The feed the articles are from.
     * @param {Object[]} articleLinks - List containing the articles for the feed.
     */
    _renderArticleLinks(subscription, articleLinks) {
        if (articleLinks.length < 1)
            logger.push("No articles for " + subscription.title + ".");

        let template = $(config.HTML_ID_ARTICLELINK_TEMPLATE).html();
        for (let i = 0; i < articleLinks.length; i++) {
            let articleLink = articleLinks[i];
            let templateAttributes = {
                articleLinkTitle: articleLink.title,
                articleLinkURL: articleLink.url,
                articleLinkFeedID: subscription.id,
                articleLinkLanguage: subscription.language,
                articleDifficultyDiscrete: articleLink.metrics.difficulty.discrete,
                articleDifficulty: Math.round(parseFloat(articleLink.metrics.difficulty.normalized) * 100) / 10,
                articleSummary: $('<p>' + articleLink.summary + '</p>').text(),
                articleIcon: subscription.image_url
            };
            let element = Mustache.render(template, templateAttributes);

            $(config.HTML_ID_ARTICLELINK_LIST).append(element);
        }

        $(config.HTML_CLASS_ARTICLELINK_FADEOUT).one('click', function (event) {
            if (!event.isPropagationStopped()) {
                event.stopPropagation();

                // Animate the click on an article.
                $(this).siblings().animate({
                    opacity: 0.25,
                }, 200, function () {
                    // Animation complete.
                    $(config.HTML_CLASS_PAGECONTENT).fadeOut();
                });

                // Log that an article has been opened.
                let url = $(this).find('a')[0].href;
                let articleInfo = {};
                url.split('?')[1].split('&').forEach(function(part) {
                    let item = part.split("=");
                    articleInfo[item[0]] = decodeURIComponent(item[1]);
                });
                UserActivityLogger.log(USER_EVENT_CLICKED_ARTICLE, url, articleInfo);
            }
        });
    }
}

import $ from 'jquery';
import Mustache from 'mustache';
import config from '../config';
import ZeeguuRequests from '../zeeguuRequests';
import swal from 'sweetalert';

/**
 * Allows the user to add feed subscriptions.
 */
export default class FeedSubscriber {
    /**
     * Link the {@link SubscriptionList} with this instance so we can update it on change.
     * @param {SubscriptionList} subscriptionList - Local (!) list of currently subscribed-to feeds.
     * @param {LanguageMenu} languageMenu - Menu to allow for switch feeds per language.
     */
    constructor(subscriptionList, languageMenu) {
        this.subscriptionList = subscriptionList;
        this.languageMenu = languageMenu;
        this.currentLanguage = 'nl'; // default
        ZeeguuRequests.get(config.GET_LEARNED_LANGUAGE, {}, 
            function (lang) {
                this.currentLanguage = lang;
            }.bind(this));
    }

    /**
     * Open the dialog window containing the list of feeds.
     * Uses the sweetalert library.
     */
    open() {
        let template = $(config.HTML_ID_ADD_SUBSCRIPTION_DIALOG_TEMPLATE).html();
        swal({
            title: 'Available Sources',
            text: template,
            html: true,
            allowOutsideClick: true,
            showConfirmButton: false,
            showCancelButton: true,
            cancelButtonText: 'Close',
        });

        this.languageMenu.load(this);
        this.load();
    }

    /**
     * Call Zeeguu and requests recommended feeds for the given language.
     * If the language is not given, it simply uses the last used language.
     * Uses {@link ZeeguuRequests}.
     * @param {string} language - Language code.
     * @example load('nl');
     */
    load(language = this.currentLanguage) {
        ZeeguuRequests.get(config.RECOMMENDED_FEED_ENDPOINT + '/' + language,
                                {}, this._loadFeedOptions.bind(this));
        this.currentLanguage = language;
    }

    /**
     * Clear the list of feed options.
     */
    clear() {
        $(config.HTML_ID_ADDSUBSCRIPTION_LIST).empty();
    }

    /**
     * Return the language for the feed options currently displayed.
     * @return {string} - The language of feed options currently on display.
     */
    getCurrentLanguage() {
        return this.currentLanguage;
    }

    /**
     * Sets the current language that user is studying (based on zeeguu settings).
     * @param {string} langauge - The language code.
     */
    _setCurrentLanguage(language) {
        this.currentLanguage = language;
    }

    /**
     * Fills the dialog's list with all the addable feeds.
     * Callback function for zeeguu.
     * @param {Object[]} data - A list of feeds the user can subscribe to.
     */
    _loadFeedOptions(data) {
        let template = $(config.HTML_ID_ADDSUBSCRIPTION_TEMPLATE).html();
        for (let i = 0; i < data.length; i++) {
            let feedOption = $(Mustache.render(template, data[i]));
            let subscribeButton = $(feedOption.find(".subscribeButton"));

            subscribeButton.click(
                function (data, feedOption, subscriptionList) {
                    return function() {
                        subscriptionList.follow(data);
                        $(feedOption).fadeOut();
                    };
            }(data[i], feedOption, this.subscriptionList));

            let feedIcon = $(feedOption.find(".feedIcon"));
            feedIcon.on( "error", function () {
                $(this).unbind("error").attr("src", "static/images/noAvatar.png");
            });
            $(config.HTML_ID_ADDSUBSCRIPTION_LIST).append(feedOption);
        }
    }
};

import $ from 'jquery';
import config from '../config';
import Mustache from 'mustache';
import ZeeguuRequests from '../zeeguuRequests';

/**
 * Retrieves the available languages of Zeeguu and fills
 * the {@link FeedSubscriber}'s dialog with these options.
 */
export default class LanguageMenu {
    /**
     * Load the available languages for the dialog.
     * Uses {@link ZeeguuRequests}.
     * @param {FeedSubscriber} feedSubscriber - List of non-subscribed feeds to update.
     */
    load(feedSubscriber) {
        let callback = ((data) => this._loadLanguageOptions(data, feedSubscriber)).bind(this);
        ZeeguuRequests.get(config.GET_AVAILABLE_LANGUAGES, {}, callback);
    }

    /**
     * Generates all the available language options as buttons in the dialog.
     * Callback function from the zeeguu request.
     * @param {string} data - JSON string of an array of language codes.
     * @param {FeedSubscriber} feedSubscriber - List of non-subscribed feeds to update.
     */
    _loadLanguageOptions(data, feedSubscriber)
    {
        let options = JSON.parse(data);
        let template = $(config.HTML_ID_LANGUAGEOPTION_TEMPLATE).html();
        options.sort();
        for (let i=0; i < options.length; ++i)
        {
            let languageOptionData = {
                languageOptionCode: options[i]
            };
            let languageOption = $(Mustache.render(template, languageOptionData));
            languageOption.on('click', function () {
                feedSubscriber.clear();
                feedSubscriber.load($(this).attr('id'));
                $(this).siblings().removeClass(config.HTML_CLASS_FOCUSED);
                $(this).addClass(config.HTML_CLASS_FOCUSED);
            });
            $("#languageOptionList").append(languageOption);
        }
        $('#' + feedSubscriber.getCurrentLanguage()).addClass(config.HTML_CLASS_FOCUSED);
    }
};

