const USER_EVENT_TEXT_TO_SPEECH = 'SPEAK TEXT';

/* Class that allows text to speech for supplied text and language. */
export default class Speaker {
    /**
     * Performs the speech synthesis of the supplied parameters.
     * @param {string} text - Text to be transformed to speech.
     * @param {string} language - Language code to be used for synthesis. 
     */
    speak(text, language) {
        let utterance = new SpeechSynthesisUtterance();
        utterance.lang = language;
        utterance.text = text;
        speechSynthesis.speak(utterance);

        UserActivityLogger.log(USER_EVENT_TEXT_TO_SPEECH, text, {language : language});
    }
};

import $ from 'jquery';
import config from '../config';
import UndoStack from './UndoStack';
import ZeeguuRequests from '../zeeguuRequests';
import UserActivityLogger from '../UserActivityLogger';

const USER_EVENT_TRANSLATE = 'TRANSLATE TEXT';
const USER_EVENT_UNDO_TRANSLATE = 'UNDO TEXT TRANSLATION';
const USER_EVENT_SEND_SUGGESTION = 'SEND SUGGESTION';

/**
 *  Class that allows for translating zeeguu tags.
 */
export default class Translator {

    /**
     * Initializes the undo manager.
     */
    constructor() {
        this.undoStack = new UndoStack();
        this.connectivesSet = this._buildConnectivesSet();
        ZeeguuRequests.get(config.GET_NATIVE_LANGUAGE, {}, function (language) {
            TO_LANGUAGE = language;
        }.bind(this));
    }

    /**
     * Merge the surrounding translated zeeguuTags
     * and insert translations for the tag's content by calling Zeeguu.
     * Uses {@link ZeeguuRequests}.
     * @param {Element} zeeguuTag - Document element containing the content to be translated.
     */
    translate(zeeguuTag) {
        this.undoStack.pushState();
        let tmp_trans = this._mergeZeeguu(zeeguuTag);

        let text = zeeguuTag.textContent.trim();
        let context = Translator._getContext(zeeguuTag);
        let url = window.location.href;
        let title = $(config.HTML_ID_ARTICLE_TITLE).text();

        let orig = document.createElement(config.HTML_ORIGINAL);
        let tran = document.createElement(config.HTML_TRANSLATED);

        $(orig).text(text);
        $(zeeguuTag).addClass(config.CLASS_LOADING);
        $(tran).attr('chosen', tmp_trans);
        $(zeeguuTag).empty().append(orig, tran);

        let callback = (data) => this._setTranslations(zeeguuTag, data);
        // Launch Zeeguu request to fill translation options.
        ZeeguuRequests.post(config.GET_TRANSLATIONS_ENDPOINT + '/' + FROM_LANGUAGE + '/' + TO_LANGUAGE,
                           {word: text, context: context, url: url, title: title}, callback);

        UserActivityLogger.log(USER_EVENT_TRANSLATE, text, {url: url, title: title, language: FROM_LANGUAGE});
    }

    /**
     * Resets to previous state (i.e. removes translation from last translated word).
     */
    undoTranslate() {
        this.undoStack.undoState();
        let url = window.location.href;
        let title = $(config.HTML_ID_ARTICLE_TITLE).text();
        UserActivityLogger.log(USER_EVENT_UNDO_TRANSLATE, '', {url: url, title: title, language: FROM_LANGUAGE});
    }

    /**
     * Sends a post request to Zeeguu about a contribution/suggestion for a translation from user.
     * @param {jQuery} $zeeguu - Zeeguu reference tag for which to send the user suggestion.
     */
    sendSuggestion ($zeeguu) {
        let word = $zeeguu.children(config.HTML_ORIGINAL).text();
        let context = Translator._getContext($zeeguu.get(0));
        let url = window.location.href;
        let title = $(config.HTML_ID_ARTICLE_TITLE).text();
        let translation = $zeeguu.children(config.HTML_TRANSLATED).attr(config.HTML_ATTRIBUTE_SUGGESTION);

        // Launch Zeeguu request to supply translation suggestion.
        ZeeguuRequests.post(config.POST_TRANSLATION_SUGGESTION + '/' + FROM_LANGUAGE + '/' + TO_LANGUAGE,
                           {word: word, context: context, url: url, title: title, translation: translation});

        UserActivityLogger.log(USER_EVENT_SEND_SUGGESTION, word, 
                               {url: url, title: title, language: FROM_LANGUAGE, translation: translation});
    }

    /**
     * Checks whether given zeeguuTag is already translated.
     * @param {jQuery} $zeeguu - Zeeguu reference tag that wraps translatable content.
     * @return {Boolean} - True only if the passed zeeguuTag already has translation data.
     */
    isTranslated($zeeguu) {
        return $zeeguu.has(config.HTML_TRANSLATED).length;
    }

    /**
     * Handle the Zeeguu request returned values. Append the returned translations.
     * @param {Element} zeeguuTag - Document element containing the original text and the translations.
     * @param {Object[]} translations - A list of translations to be added to the given htmlTag content.
     */
    _setTranslations(zeeguuTag, translations) {
        var tran = zeeguuTag.children[1];
        translations = translations.translations;
        var transCount = Math.min(translations.length, 3);
        tran.setAttribute(config.HTML_ATTRIBUTE_TRANSCOUNT, transCount);
        for (var i = 0; i < transCount; i++)
            tran.setAttribute(config.HTML_ATTRIBUTE_TRANSLATION + i, translations[i].translation);

        tran.setAttribute(config.HTML_ATTRIBUTE_CHOSEN, translations[0].translation); // default chosen translation is 0
        tran.setAttribute(config.HTML_ATTRIBUTE_SUGGESTION, '');
        $(zeeguuTag).removeClass(config.CLASS_LOADING);
    }

    /**
     * Returns surrounding textual context for a given zeeguuTag by extracting the text from
     * its wrapping parent element.
     * @param {Element} zeeguuTag - Document element for which to extract textual context
     * @return {string} - Textual context.
     */
    static _getContext(zeeguuTag) {
        let zeeguuParentClone = zeeguuTag.parentElement.cloneNode(true);        
        $(zeeguuParentClone).find(config.HTML_ID_ALTERMENU).remove();
        return zeeguuParentClone.textContent;
    }

    /**
     * Merge the translated zeeguuTags surrounding the given zeeguuTag.
     * @param {Element} zeeguuTag - Zeeguu tag for which to perform merge with the surrounding tags.
     * @return {string} - Temporary mock translation text.
     */
    _mergeZeeguu(zeeguuTag) {
        var tmp_trans = '';
        var connectives = '';
        var padding_len = zeeguuTag.textContent.length; // approximates the size of the to be translated word

        var node = zeeguuTag.previousSibling;

        while (node && this.connectivesSet.has(node.textContent.trim())) {
            connectives = node.textContent + connectives;
            node = node.previousSibling;
        }

        if (node && node.nodeName == config.HTML_ZEEGUUTAG && this.isTranslated($(node))) {
            zeeguuTag.textContent = node.textContent + connectives + zeeguuTag.textContent;
            tmp_trans = tmp_trans.concat($(node).find('tran').attr('chosen'));
            node.parentNode.removeChild(node.nextSibling);
            node.parentNode.removeChild(node);
        }

        tmp_trans = tmp_trans.concat(' ' + '..'.repeat(padding_len) + ' ');
        connectives = '';
        node = zeeguuTag.nextSibling;
        while (node && this.connectivesSet.has(node.textContent.trim())) {
            connectives += node.textContent;
            node = node.nextSibling;
        }

        if (node && node.nodeName == config.HTML_ZEEGUUTAG && this.isTranslated($(node))) {
            zeeguuTag.textContent += connectives + node.textContent;
            tmp_trans = tmp_trans.concat($(node).find('tran').attr('chosen'));
            node.parentNode.removeChild(node.previousSibling);
            node.parentNode.removeChild(node);
        }
        return tmp_trans;
    }

    /**
     * Build the used connectives set, for which the merge function will merge over.
     * @return {Set} - The set containing the available connectives.
     */
    _buildConnectivesSet() {
        let set = new Set();
        set.add('');
        set.add('-');
        set.add(':');
        set.add(';');
        set.add('\'');
        set.add('’');
        set.add('‘');
        return set;
    }

};

import $ from 'jquery';
import config from '../config'

/**
 * Class that allows for saving the state of the page content after each
 * translation and restore upon request.
 */
export default class UndoStack {
    /**
     * Initialize the stack for saving the states.
     */
    constructor() {
        this.stack = [];
    }

    /**
     * Push state onto the stack.
     */
    pushState() {
        let $saved = $(config.HTML_CLASS_PAGECONTENT).clone();
        let $zeeguu = $saved.find(config.HTML_ZEEGUUTAG + config.HTML_CLASS_LOADING);
        let word = $zeeguu.text();
        $zeeguu.empty().removeClass(config.CLASS_LOADING).text(word);
        this.stack.push($saved);
    }


    /** Revert to previous state stored on the stack. */
    undoState() {
        var $saved = this.stack.pop();
        if ($saved) {
            $(config.HTML_CLASS_PAGECONTENT).remove();
            $(config.HTML_CLASS_CONTENTCONTAINER).prepend($saved);    
        }
    }
};

import $ from 'jquery';
import config from '../config';
import Translator from './Translator';
import AlterMenu from './AlterMenu'
import Speaker from './Speaker';
import Starer from '../Starer';
import UserActivityLogger from '../UserActivityLogger';

import '../../../styles/mdl/material.min.js';
import '../../../styles/mdl/material.min.css';
import '../../../styles/article.css';
import '../../../styles/material-icons.css';

/* Script that binds listeners to html events, such that the
 * correct object is called to handle it. */
const translator = new Translator();
const alterMenu = new AlterMenu();
const speaker = new Speaker();
const starer = new Starer();

const USER_EVENT_ENABLE_COPY = 'ENABLE COPY';
const USER_EVENT_DISABLE_COPY = 'DISABLE COPY';
const USER_EVENT_CHANGE_ORIENTATION = 'CHANGE ORIENTATION';
const USER_EVENT_LIKE_ARTICLE = 'LIKE ARTICLE';

const STAR_BORDER = 'star_border';

/* When the document has finished loading,
 * bind all necessary listeners. */
$(document).ready(function() {
    // Disable selection by default.
    disableToggleCopy();
    attachZeeguuListeners();
    setStarerState();

    /* When the copy toggle is switched on,
     * copying is enabled and translation gets disabled and vice-versa. */
    $(config.HTML_ID_TOGGLECOPY).click(function()
    {
        // Selection is disabled -> enable it.
        if ($(this).hasClass('mdl-button--disabled')) enableToggleCopy();
        else disableToggleCopy();
    });

    /* When the undo is clicked, content page is replaced
     * with previous one in the stack and listeners are re-attached. */
    $(config.HTML_ID_TOGGLEUNDO).click(function()
    {
        if (alterMenu.isOpen()) {
            alterMenu.close();
            return;
        }
        $(config.HTML_ZEEGUUTAG).off();
        translator.undoTranslate();
        attachZeeguuListeners();
    });

    /* When the like button is clicked, set its background color. */
    $(config.HTML_ID_TOGGLELIKE).click(function()
    {
        $(this).toggleClass('mdl-button--disabled');

        let url = $(config.HTML_ID_ARTICLE_URL).children('a').attr('href');
        let title = $(config.HTML_ID_ARTICLE_TITLE).text();
        UserActivityLogger.log(USER_EVENT_LIKE_ARTICLE, url, {title: title});
    });

    /* Toggle listener for star button. */
    $(config.HTML_ID_TOGGLESTAR).click(function()
    {
        starer.toggle();
    });
});

/* Clicking anywhere in the document when the
 * alter menu is open, except for the input field,
 * will close the alter menu.*/
$(document).click(function(event) {
    let $target = $(event.target);
    if (!$target.is('input') && alterMenu.isOpen()) {
        alterMenu.close();
    } else if ($target.is('input') && $target.val() === config.TEXT_SUGGESTION) {
        $target.attr('value', '');
    }
});

/* Listens on keypress 'enter' to set the user suggestion
 * as the chosen translation and sends the user's contribution
 * to Zeeguu. */
$(document).keypress(function(event) {
    let $target = $(event.target);
    if ($target.is('input') && event.which === config.ENTER_KEY) {
        let $zeeguu = $target.closest(config.HTML_ZEEGUUTAG);
        let $trans  = $zeeguu.children(config.HTML_TRANSLATED);
        if ($target.val() !== '') {
            $trans.attr(config.HTML_ATTRIBUTE_CHOSEN, $target.val());
            $trans.attr(config.HTML_ATTRIBUTE_SUGGESTION, $target.val());
            translator.sendSuggestion($zeeguu);
        }
        alterMenu.close();
    }
});

/* Every time the screen orientation changes,
 * the alter menu will be closed. */
$(window).on("orientationchange",function() {
  alterMenu.close();
  UserActivityLogger.log(USER_EVENT_CHANGE_ORIENTATION);
});

/* Disable selection. */
function disableToggleCopy() {
    $("p").each (function () {
        $(this).addClass(config.CLASS_NOSELECT);
    });
    $(config.HTML_ID_TOGGLECOPY).addClass('mdl-button--disabled');
    UserActivityLogger.log(USER_EVENT_DISABLE_COPY);
}

/* Enable selection. */
function enableToggleCopy() {
    $("p").each (function () {
        $(this).removeClass(config.CLASS_NOSELECT);
    });
    $(config.HTML_ID_TOGGLECOPY).removeClass('mdl-button--disabled');
    UserActivityLogger.log(USER_EVENT_ENABLE_COPY);
}

function isToggledCopy() {
    return !$(config.HTML_ID_TOGGLECOPY).hasClass('mdl-button--disabled');
}

function setStarerState() {
    starer.setState($('.material-icons.star.off').text() === STAR_BORDER);
}

/* Attach Zeeguu tag click listener. */
function attachZeeguuListeners () {
    /* When a translatable word has been clicked,
     * either try to translate it, speak it, or open an alternative
     * translation window.  */
    $(config.HTML_ZEEGUUTAG).click(function(event) {
        if (isToggledCopy())
            return;
        if (alterMenu.isOpen())
            return;

        let $target = $(event.target);
        if ( $target.is(config.HTML_ZEEGUUTAG) && !translator.isTranslated($target) ) {
            // A non-translated word is clicked, so we translate it.
            translator.translate(this);
        } else if ($target.is(config.HTML_ORIGINAL) ) {
            // Original text is clicked, so we pronounce it using the speaker.
            speaker.speak($target.text(), FROM_LANGUAGE);
        } else if ($target.is(config.HTML_TRANSLATED) ) {
            // Translated text is clicked, so we open the alterMenu to allow for suggestions.
            alterMenu.build($target);
        }
    });
}

import $ from 'jquery';
import config from './config'

/**
 * Abstracts request to the Zeeguu API.
 * @see https://www.zeeguu.unibe.ch
 */
export default class ZeeguuRequests {
    /**
     * Retrieve the Zeeguu sessionID.
     * @returns {?string} session - The session ID of the user, if present.
     */
    static session () {
        return this._readCookie(config.ZEEGUU_SESSION);
    }

    /**
     * Read a cookie, search for the attribute of a particular name and retrieve it.
     * @param {string} name - The name of the attribute.
     * @returns {?string} session - The session ID of the user, if present.
     */
    static _readCookie(name) {
            var nameEQ = name + "=";
            var ca = document.cookie.split(';');
            for (var i = 0; i < ca.length; i++) {
                var c = ca[i];
                while (c.charAt(0) == ' ') c = c.substring(1, c.length);
                if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
            }
            return '';
        }

    /**
     * Send a GET request to the Zeeguu API.
     * @param {string} endpoint - The endpoint to use.
     * @param {string[]} requestData - Parameters to append.
     * @param {function(data : string)} responseHandler - A function that can asynchronously handle the reply.
     */
    static get (endpoint, requestData, responseHandler = function() {}) {
        requestData.session = this.session();
        $.get(
            config.ZEEGUU_SERVER + endpoint,
            requestData,
            responseHandler
        );
    }

    /**
     * Send a POST request to the Zeeguu API.
     * @param {string} endpoint - The endpoint to use.
     * @param {string[]} requestData - Parameters to append.
     * @param {function(data : string)} responseHandler - A function that can asynchronously handle the reply.
     */
    static post (endpoint, requestData, responseHandler = function() {}) {
        $.post(
            config.ZEEGUU_SERVER + endpoint + "?session=" + this.session(),
            requestData,
            responseHandler
        );
    }
}
