import $ from 'jquery';
import config from '../config'
import Notifier from './Notifier'

/**
 * Class that allows for choosing alternative zeeguu translations from
 * a drop-down alter menu.
 */
export default class AlterMenu {
    /**
     * Initialize the notifier field and the control field for the state of the
     * alter menu (i.e. open or closed).
     */
    constructor() {
        this.menuOpen = false;
        this.notifier = new Notifier();
    }

    /**
     * Create and open the alternative translation menu. 
     * @param {Element} htmlTag - Document element for which to present the alter menu.
     */
    constructAndOpen(htmlTag) {
        // Check how many alternatives there are, if less than 2: abort.
        var transCount = parseInt(htmlTag.getAttribute(config.HTML_ATTRIBUTE_TRANSCOUNT));
        if (transCount < 2) {
            this.notifier.notify("Sorry, no alternatives.");
            return;
        }
        this.construct(htmlTag, transCount);
        this._place(htmlTag);
        $(config.HTML_ID_ALTERMENU).slideDown(function () {
            this.menuOpen = true
        }.bind(this));
    };

    /**
     * Add buttons with click listeners that replace the translation and
     * append these to the alter menu. 
     * @param {Element} zeeguuTag - Tag from which the alternative transaltions are retrieved.
     * @param {int} transCount - Number of present alternative translations. 
     */
    construct(zeeguuTag, transCount) {
        $(config.HTML_ID_ALTERMENU).empty();
        for (var i = 0; i < transCount; i++) {
            var button = document.createElement('button');
            var alternative = zeeguuTag.getAttribute(config.HTML_ATTRIBUTE_TRANSLATION + i);
            button.textContent = alternative;
            $(button).addClass("mdl-button").addClass("mdl-js-button").addClass("mdl-js-ripple-effect");
            $(config.HTML_ID_ALTERMENU).append($(button));
            $(button).click({zeeguuTag: zeeguuTag, alternative: i}, this._swapPrimaryTranslation);
        }
    }

    /**
     * Swap the currently set translation (config.HTML_ATTRIBUTE_TRANSLATION 0) with the selected alternative.
     * @param {Object} selectedAlternative - Attribute that determines the selected alternative.  
     */
    _swapPrimaryTranslation(selectedAlternative) {
        var zeeguuTag = selectedAlternative.data.zeeguuTag;
        var alternative = selectedAlternative.data.alternative;
        var oldText = zeeguuTag.getAttribute(config.HTML_ATTRIBUTE_TRANSLATION + '0');
        var newText = zeeguuTag.getAttribute(config.HTML_ATTRIBUTE_TRANSLATION + alternative);
        zeeguuTag.setAttribute(config.HTML_ATTRIBUTE_TRANSLATION + '0', newText);
        zeeguuTag.setAttribute(config.HTML_ATTRIBUTE_TRANSLATION + alternative, oldText);
    }

    /**
     * Place the alter menu below the supplied zeeguuTag.
     * @param {Element} zeeguuTag - Reference tag for the placement of the alter menu.
     */
    _place(zeeguuTag) {
        var position = $(zeeguuTag).position();
        var tagHeight = $(zeeguuTag).outerHeight();
        var tagWidth = $(zeeguuTag).outerWidth();
        var menuWidth = $(config.HTML_ID_ALTERMENU).outerWidth();
        var topScroll = $(".mdl-layout__content").scrollTop();
        $(zeeguuTag).append($(config.HTML_ID_ALTERMENU));
        $(config.HTML_ID_ALTERMENU).css({
            position: "absolute",
            maxWidth: "80%",
            display: "inline-block",
            left: position.left + (tagWidth - menuWidth) / 2 + "px",
            top: position.top + tagHeight + topScroll + "px"
        });
        $(config.HTML_ID_ALTERMENU).hide();
    }

    /**
     * Update the position of the alter menu.
     */
    reposition() {
        this._place($(config.HTML_ID_ALTERMENU).parent());
        $(config.HTML_ID_ALTERMENU).show();
    };

    /**
     *  Hide the alter menu. 
     */
    close() {
        $(config.HTML_ID_ALTERMENU).slideUp(function () {
            $(config.HTML_ID_ALTERMENUCONTAINER).append($(config.HTML_ID_ALTERMENU));
            this.menuOpen = false;
        }.bind(this));
    };

    /**
     * Check whether the alter menu is an open state.
     * @return {boolean} - True only if the alter menu is open.
     */
    isOpen() {
        return this.menuOpen;
    };
};

import $ from 'jquery';

/**
 * Presents small pop-up messages (notifications).
 * Does not repeat the same message if it currently displays it.
 */
export default class Notifier {
    /**
     * Initializes the previously displayed message field. Empty at creation.
     */
    constructor() {
        this.lastMessage = "";
    };

    /**
     * Notify the user with the supplied message.
     * @param {string} message - Message to be displayed. 
     */
    notify (message) {
        var snackbar = document.querySelector('.mdl-js-snackbar');
        if (this.lastMessage == message && $(snackbar).hasClass('mdl-snackbar--active'))
            return;
        snackbar.MaterialSnackbar.showSnackbar({message: message});
        this.lastMessage = message;
    };
};

import $ from 'jquery';
import ZeeguuRequests from '../zeeguuRequests'
import config from '../config'

/**
 *  Class that allows for translating zeeguu tags. 
 */
export default class Translator {
    /**
     * Merge the surrounding translated zeeguuTags
     * and insert translations for the tag's content by calling Zeeguu.
     * Uses {@link ZeeguuRequests}.
     * @param {Element} zeeguuTag - Document element containing the content to be translated. 
     */
    translate(zeeguuTag) {
        this._mergeZeeguu(zeeguuTag);

        var text = zeeguuTag.textContent.trim();
        var context = this._getContext(zeeguuTag);
        var url = $(config.HTML_ID_ARTICLE_URL).text();
        var title = $(config.HTML_ID_ARTICLE_TITLE).text();

        $(zeeguuTag).empty(); // clear tag up for insertion
        var orig = document.createElement(config.HTML_ORIGINAL);
        var tran = document.createElement(config.HTML_TRANSLATED);
        $(orig).text(text);
        $(orig).addClass('loading');
        $(zeeguuTag).append(orig, tran);


        var callback = (data) => this._setTranslations(orig, tran, data);
        // Launch Zeeguu request to fill translation options.
        ZeeguuRequests.post(config.GET_TRANSLATIONS_ENDPOINT + '/' + FROM_LANGUAGE + '/' + config.TO_LANGUAGE,
                           {word: text, context: context, url: url, title: title}, callback);
    }

    /**
     * Checks whether given zeeguuTag is already translated.
     * @param {Element} zeeguuTag - Document element that wraps translatable content. 
     * @return {Boolean} - True only if the passed zeeguuTag already has translation data.    
     */
    isTranslated(zeeguuTag) {
        return $(zeeguuTag).has(config.HTML_TRANSLATED).length;
    }

    /**
     * Handle the Zeeguu request returned values. Append the returned translations.
     * @param {Element} htmlTag - Document element processed for translation.
     * @param {Object[]} translations - A list of translations to be added to the given htmlTag content. 
     */
    _setTranslations(orig, htmlTag, translations) {
        translations = translations.translations;
        var transCount = Math.min(translations.length, 3);
        htmlTag.setAttribute(config.HTML_ATTRIBUTE_TRANSCOUNT, transCount);
        for (var i = 0; i < transCount; i++)
            htmlTag.setAttribute(config.HTML_ATTRIBUTE_TRANSLATION + i, translations[i].translation);

        $(orig).removeClass('loading');
    }

    /**
     * Returns surrounding textual context for a given zeeguuTag by extracting the text from 
     * its wrapping parent element.
     * @param {Element} zeeguuTag - Document element for which to extract textual context
     * @return {string} - Textual context.
     */
    _getContext(zeeguuTag) {
        return zeeguuTag.parentElement.textContent;
    }

    /**
     * Merge the translated zeeguuTags surrounding the given zeeguuTag. 
     * @param {Element} zeeguuTag - Tag for which to perform merge with the surrounding tags. 
     */
    _mergeZeeguu(zeeguuTag) {
        var spaces = '';
        var node = zeeguuTag.previousSibling;
        while (node && node.textContent == ' ') {
            node = node.previousSibling;
            spaces += ' ';
        }
        if (node && node.nodeName == config.HTML_ZEEGUUTAG && this.isTranslated(node)) {
            zeeguuTag.textContent = node.textContent + spaces + zeeguuTag.textContent;
            node.parentNode.removeChild(node);
        }
        spaces = '';
        node = zeeguuTag.nextSibling;
        while (node && node.textContent == ' ') {
            node = node.nextSibling;
            spaces += ' ';

        }
        if (node && node.nodeName == config.HTML_ZEEGUUTAG && this.isTranslated(node)) {
            zeeguuTag.textContent += spaces + node.textContent;
            node.parentNode.removeChild(node);
        }
    }

};

import $ from 'jquery';
import config from '../config';
import Translator from './Translator';
import AlterMenu from './AlterMenu'
import Speaker from '../Speaker';

/* Script that binds listeners to html events, such that the
 * correct object is called to handle it. */
var translator = new Translator();
var alterMenu = new AlterMenu();
var speaker = new Speaker();

/* When the document has finished loading,
 * bind all necessary listeners. */
$(document).ready(function() {
    disableHREF();

    /* If you click anywhere in the translatable window,
     * and the alterMenu is open, we close it. */
    $('.translatable').click(function() {
        if (alterMenu.isOpen())
            alterMenu.close();
    });

    /* When the translate toggle is changed, we
     * make sure that we disable or enable hyperlinks
     * and close all translation tools. */
    $(config.HTML_ID_TOGGLETRANSLATE).change(function()
    {
        if (this.checked)
            disableHREF();
        else
        {
            alterMenu.close();
            enableHREF();
        }
    });

    /* When a translatable word has been clicked,
     * either try to translate it, speak it, or open an alternative
     * translation window.  */
    $(config.HTML_ZEEGUUTAG).click(function(event) {
        if (!$(config.HTML_ID_TOGGLETRANSLATE).is(':checked'))
            return;

        if (alterMenu.isOpen()) {
            alterMenu.close();
            return;
        }

        var target = $(event.target);
        if ( target.is(config.HTML_ZEEGUUTAG) ) {
            if (!translator.isTranslated(this)) {
                translator.translate(this);
            }
        } else if (target.is(config.HTML_ORIGINAL) ) {
            speaker.speak($(this).find(config.HTML_ORIGINAL).text(), FROM_LANGUAGE);
        } else if (target.is(config.HTML_TRANSLATED) ) {
            alterMenu.constructAndOpen(this.children[1]);
        }
    });
});

/* Every time the screen changes, we need to
 * reposition the alter menu to be at the correct word
 * position. */
$(window).on("resize", function() {
    if (alterMenu.isOpen())
        alterMenu.reposition();
});

/* Disable links.
 * Done in this peculiar way as default link disabling methods do not
 * pass a proper text selection. */
function disableHREF()
{
    $('.translatable').find('a').each(function()
    {
        this.setAttribute('href_disabled',this.getAttribute('href'));
        this.removeAttribute('href');
    });
}

/* Enable links. */
function enableHREF()
{
    $('.translatable').find('a').each(function()
    {
        this.setAttribute('href',this.getAttribute('href_disabled'));
        this.removeAttribute('href_disabled');
    });
}

import $ from 'jquery';
import config from './config'

/**
 * Abstracts request to the Zeeguu API.
 * @see https://www.zeeguu.unibe.ch
 */
export default class ZeeguuRequests {
    /**
     * Retrieve the Zeeguu sessionID.
     * @returns {?string} session - The session ID of the user, if present.
     */
    static session () {
        return this._readCookie(config.ZEEGUU_SESSION);
    }

    /**
     * Read a cookie, search for the attribute of a particular name and retrieve it.
     * @param {string} name - The name of the attribute.
     * @returns {?string} session - The session ID of the user, if present.
     */
    static _readCookie(name) {
            var nameEQ = name + "=";
            var ca = document.cookie.split(';');
            for (var i = 0; i < ca.length; i++) {
                var c = ca[i];
                while (c.charAt(0) == ' ') c = c.substring(1, c.length);
                if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
            }
            return '';
        }

    /**
     * Send a GET request to the Zeeguu API.
     * @param {string} endpoint - The endpoint to use.
     * @param {string[]} requestData - Parameters to append.
     * @param {function(data : string)} responseHandler - A function that can asynchronously handle the reply.
     */
    static get (endpoint, requestData, responseHandler) {
        requestData.session = this.session();
        $.get(
            config.ZEEGUU_SERVER + endpoint,
            requestData,
            responseHandler
        );
    }

    /**
     * Send a POST request to the Zeeguu API.
     * @param {string} endpoint - The endpoint to use.
     * @param {string[]} requestData - Parameters to append.
     * @param {function(data : string)} responseHandler - A function that can asynchronously handle the reply.
     */
    static post (endpoint, requestData, responseHandler) {
        $.post(
            config.ZEEGUU_SERVER + endpoint + "?session=" + this.session(),
            requestData,
            responseHandler
        );
    }
}
