import $ from 'jquery';
import Mustache from 'mustache';
import config from '../config';
import ZeeguuRequests from '../zeeguuRequests';

/**
 * Manages a list of article links.
 */
export default class ArticleList {
    /**
     * Call zeeguu and get the articles for the given feed 'subscription'.
     * Uses {@link ZeeguuRequests}.
     * @param {Object} subscription - The feed to retrieve articles from.
     */
    load(subscription) {
        $(config.HTML_CLASS_LOADER).show();
        var callback = (data) => this._loadArticleLinks(subscription, data);
        ZeeguuRequests.get(config.GET_FEED_ITEMS + '/' + subscription.subscriptionID, {}, callback);
    };

    /**
     * Remove all articles from the list.
     */
    clear() {
        $(config.HTML_ID_ARTICLELINK_LIST).empty();
    };

    /**
     * Remove all articles from the list associated with the given feed.
     * @param {string} feedID - The identification code associated with the feed.
     */
    remove(feedID) {
        $('li[articleLinkFeedID="' + feedID + '"]').remove();
    };

    /**
     * Generate all the article links from a particular feed.
     * Callback function for the zeeguu request.
     * @param {Object} subscriptionData - The feed the articles are from.
     * @param {Object[]} data - List containing the articles for the feed.
     */
    _loadArticleLinks(subscriptionData, data) {
        var template = $(config.HTML_ID_ARTICLELINK_TEMPLATE).html();
        for (var i = 0; i < data.length; i++) {
            var difficulty = Math.round(parseFloat(data[i].metrics.difficulty.normalized) * 100) / 10;
            var articleLinkData = {
                articleLinkTitle: data[i].title,
                articleLinkURL: data[i].url,
                articleLinkFeedID: subscriptionData.subscriptionID,
                articleLinkLanguage: subscriptionData.subscriptionLanguage,
                articleDifficultyDiscrete: data[i].metrics.difficulty.discrete,
                articleDifficulty: difficulty,
                articleSummary: $('<p>' + data[i].summary + '</p>').text(),
                articleIcon: subscriptionData.subscriptionIcon
            };
            $(config.HTML_ID_ARTICLELINK_LIST).append(Mustache.render(template, articleLinkData));
        }
        $(config.HTML_CLASS_LOADER).fadeOut('slow');
    }
};

import $ from 'jquery';
import Mustache from 'mustache';
import config from '../config';
import ZeeguuRequests from '../zeeguuRequests';

/**
 * Allows the user to add feed subscriptions.
 */
export default class FeedSubscriber {
    /**
     * Link the {@link SubscriptionList} with this instance so we can update it on change.
     * @param {SubscriptionList} subscriptionList - Local (!) list of currently subscribed-to feeds.
     */
    constructor(subscriptionList) {
        this.subscriptionList = subscriptionList;
        this.currentLanguage = 'nl';
    }

    /**
     * Call Zeeguu and requests recommended feeds for the given language.
     * If the language is not given, it simply uses the last used language.
     * Uses {@link ZeeguuRequests}.
     * @param {string} language - Language code.
     * @example load('nl');
     */
    load(language) {
        language = typeof language !== 'undefined' ? language : this.currentLanguage;
        ZeeguuRequests.get(config.RECOMMENDED_FEED_ENDPOINT + '/' + language,
                                {}, this._loadFeedOptions.bind(this));
        this.currentLanguage = language;
    };

    /**
     * Clear the list of feed options.
     */
    clear() {
        $(config.HTML_ID_ADDSUBSCRIPTION_LIST).empty();
    };

    /**
     * Return the language for the feed options currently displayed.
     * @return {string} - The language of feed options currently on display.
     */
    getCurrentLanguage() {
        return this.currentLanguage;
    };

    /**
     * Fills the dialog's list with all the addable feeds.
     * Callback function for zeeguu.
     * @param {Object[]} data - A list of feeds the user can subscribe to.
     */
    _loadFeedOptions(data) {
        var template = $(config.HTML_ID_ADDSUBSCRIPTION_TEMPLATE).html();
        for (var i = 0; i < data.length; i++) {
            var addableData = {
                addableTitle: data[i]['title'],
                addableID: data[i]['id'],
                addableImage: data[i]['image_url']
            };
            var feedOption = $(Mustache.render(template, addableData));
            var subscribeButton = $(feedOption.find(".subscribeButton"));
            var _follow = this._follow.bind(this);
            subscribeButton.click(function () {
                _follow($(this).parent());
            });
            var feedIcon = $(feedOption.find(".feedIcon"));
            feedIcon.on( "error", function () {
                $(this).unbind("error").attr("src", "static/images/noAvatar.png");
            });
            $(config.HTML_ID_ADDSUBSCRIPTION_LIST).append(feedOption);
        }
    }

    /**
     * Subscribe to a new feed, calls the zeeguu server.
     * Uses {@link ZeeguuRequests}.
     * @param {Element} feed - Document element containing the id of the feed.
     */
    _follow(feed) {
        var feedID = $(feed).attr('addableID');
        var callback = ((data) => this._onFeedFollowed(feed, data)).bind(this);
        ZeeguuRequests.post(config.FOLLOW_FEED_ENDPOINT, {feed_id: feedID}, callback);
    }

    /**
     * A feed has just been followed, so we refresh the {@link SubscriptionList} and remove the
     * mentioned feed from the addable feed list.
     * Callback function for Zeeguu.
     * @param {Element} feed - Document element containing the id of the feed.
     * @param {string} data - Reply from the server.
     */
    _onFeedFollowed(feed, data) {
        if (data == "OK") {
            this.subscriptionList.load();
            $(feed).fadeOut();
        }
    }
};

import $ from 'jquery';
import config from '../config';
import Mustache from 'mustache';
import ZeeguuRequests from '../zeeguuRequests';

/**
 * Retrieves the available languages of Zeeguu and fills
 * the {@link FeedSubscriber}'s dialog with these options.
 */
export default class LanguageMenu {
    /**
     * Link the {@link FeedSubscriber} to this instance.
     * @param {FeedSubcriber} feedSubscriber - List of non-subscribed feeds to update.
     */
    constructor(feedSubscriber) {
        this.feedSubscriber = feedSubscriber;
    }

    /**
     * Load the available languages for the dialog.
     * Uses {@link ZeeguuRequests}.
     */
    load() {
        ZeeguuRequests.get(config.GET_AVAILABLE_LANGUAGES, {}, this._loadLanguageOptions.bind(this));
    }

    /**
     * Generates all the available language options as buttons in the dialog.
     * Callback function from the zeeguu request.
     * @param {string} data - JSON string of an array of language codes.
     */
    _loadLanguageOptions(data)
    {
        var options = JSON.parse(data);
        var template = $(config.HTML_ID_LANGUAGEOPTION_TEMPLATE).html();
        options.sort();
        for (var i=0; i < options.length; ++i)
        {
            var languageOptionData = {
                languageOptionCode: options[i]
            }
            var languageOption = $(Mustache.render(template, languageOptionData));
            var feedSubscriber = this.feedSubscriber;
            languageOption.on('click', function () {
                feedSubscriber.clear();
                feedSubscriber.load($(this).attr('id'));
            });
            $("#languageOptionList").append(languageOption);
        }
    }
};

import $ from 'jquery'
import Mustache from 'mustache';
import config from '../config';
import ZeeguuRequests from '../zeeguuRequests';

/**
 * Shows a list of all subscribed feeds, allows the user to remove them.
 * It updates the {@link ArticleList} accordingly.
 */
export default class SubscriptionList {
    /**
     * Bind with the {@link ArticleList} and initialise an empty list of feeds.
     * @param {ArticleList} articleList - List of all articles available to the user.
     */
    constructor(articleList) {
        this.articleList = articleList;
        this.feedList = new Set();
    }

    /**
     *  Call zeeguu and retrieve all currently subscribed feeds.
     *  Uses {@link ZeeguuRequests}.
     */
    load() {
        ZeeguuRequests.get(config.GET_FEEDS_BEING_FOLLOWED, {}, this._loadSubscriptions.bind(this));
    };

    /**
     * Remove all feeds from the list, clear {@link ArticleList} as well.
     */
    clear() {
        $(config.HTML_ID_SUBSCRIPTION_LIST).empty();
        this.articleList.clear();
    };

    /**
     * Call clear and load successively.
     */
    refresh() {
        // Refresh the feed list.
        this.clear();
        this.load();
    };

    /**
     * Fills the subscription list with all the subscribed feeds.
     * Callback function for the zeeguu request,
     * makes a call to {@link ArticleList} in order to load the feed's associated articles.
     * @param {Object[]} data - List containing the feeds the user is subscribed to.
     */
    _loadSubscriptions(data) {
        var template = $(config.HTML_ID_SUBSCRIPTION_TEMPLATE).html();
        for (var i = 0; i < data.length; i++) {
            var subscriptionData = {
                subscriptionTitle: data[i]['title'],
                subscriptionID: data[i]['id'],
                subscriptionLanguage: data[i]['language'],
                subscriptionIcon: data[i]['image_url']
            };
            var subscription = $(Mustache.render(template, subscriptionData));
            var removeButton = $(subscription.find(".removeButton"));
            var _unfollow = this._unfollow.bind(this);
            removeButton.click(function() {
                _unfollow($(this).parent());
            });
            if (!this.feedList.has(Number(subscriptionData['subscriptionID']))) {                
                $(config.HTML_ID_SUBSCRIPTION_LIST).append(subscription);
                this.articleList.load(subscriptionData);
            }             
            this.feedList.add(Number(subscriptionData['subscriptionID']));
        }
    }

    /**
     * Un-subscribe from a feed, call the zeeguu server.
     * Uses {@link ZeeguuRequests}.
     * @param {Element} feed - Feed element of the list to un-subscribe from.
     */
    _unfollow(feed) {
        var removableID = $(feed).attr('removableID');
        var callback = ((data) => this._onFeedUnfollowed(feed, data)).bind(this);
        ZeeguuRequests.get(config.UNFOLLOW_FEED_ENDPOINT + "/" + removableID,
                            {}, callback);
    }

    /**
     * A feed has just been removed, so we remove the mentioned feed from the subscription list.
     * Callback function for zeeguu.
     * @param {Element} feed - Feed element of the list that is to be removed.
     * @param {string} data - Server reply.
     */
    _onFeedUnfollowed(feed, data) {
        if (data == "OK") {
            this._remove(feed);
        }
    }

    /**
     * Remove a mentioned feed from the local list (not from the zeeguu list).
     * Makes sure the associated articles are removed as well by notifying {@link ArticleList}.
     * @param {Element} feedNode - The document element (feed) to remove.
     */
    _remove(feedNode) {
        var feedID = $(feedNode).attr('removableID');
        this.articleList.remove(feedID);
        if (!this.feedList.delete(Number(feedID)))  { console.log("Error"); }
        $(feedNode).fadeOut();
    }
};

import $ from 'jquery';
import ArticleList from './ArticleList';
import SubscriptionList from './SubscriptionList';
import FeedSubscriber from './FeedSubscriber';
import dialogPolyfill from 'dialog-polyfill';
import LanguageMenu from './LanguageMenu';

/* Script that binds listeners to html events, such that the
 * correct object is called to handle it. */
var articleList = new ArticleList();
var subscriptionList = new SubscriptionList(articleList);
var feedSubscriber = new FeedSubscriber(subscriptionList);
var languageMenu = new LanguageMenu(feedSubscriber);


/* When the document has finished loading,
 * bind all necessary listeners. */
$(document).ready(function() {
    subscriptionList.load();
    feedSubscriber.load();
    languageMenu.load();

    var addFeedDialog = document.querySelector('dialog');
    var showAddFeedDialogButton = document.querySelector('.show-modal');

    // Some browsers do not support dialog, for that we use Polyfill.
    if (!addFeedDialog.showModal)
        dialogPolyfill.registerDialog(addFeedDialog);

    // Open and closing of the dialog is handled here.
    showAddFeedDialogButton.addEventListener('click', function () {
        addFeedDialog.showModal();
        $('#' + feedSubscriber.getCurrentLanguage()).focus();
    });

    addFeedDialog.querySelector('.close').addEventListener('click', function () {
        addFeedDialog.close();
    });
});

